#!/usr/bin/perl

use 5.12.0;
use strict;
use Data::Dumper;


# Since I am using Perl, taking liberty of considering no. as string !
my $number = '';

# Psuedo Code
## Take substring, if substring contains 0, disregard, since product would be 0
## Else take product, check if it is bigger than previous product and so on ! Largest product is the answer

#my @num_arr = split('', $number);
my $i=0;
my $max_iter = length($number) - 13;

#print "Size of the actual string is ".scalar(@num_arr)."\n";
print "Size of max_iter is ".$max_iter."\n";

#Get the substring
#my $val = substr $number, 0, 13;
#print "$val\n";
my $max_product_curr=1;
my $max_product_prev=1;

for($i=0; $i<$max_iter; $i++)
{
	my $temp_str = substr $number, $i, 13;
	$max_product_curr = 1;
	if( index($temp_str,'0') != -1 )
	{
		$max_product_curr = 0;
	}
	else
	{
		my @temp_arr = split('', $temp_str);
		for(my $j=0; $j<scalar(@temp_arr); $j++)
		{
			$max_product_curr = $max_product_curr * $temp_arr[$j];
		}
	}
	
	print "Processing string $temp_str =>  $max_product_curr \n";
	if($max_product_curr >= $max_product_prev)
	{
		$max_product_prev = $max_product_curr;
	}
}

print "Max product is $max_product_prev\n";